<?php	function randomsalting() {		$salt = "";		$characters = array_merge(range('A','Z'), range('a','z'), range('0','9'));		$max = count($characters) - 1;		for ($i = 0; $i < 6; $i++) {			$rand = mt_rand(0, $max);			$salt .= $characters[$rand];		}		return $salt;	}		function encryption($password) {		$salt = randomsalting();		$hash = crypt($password, $salt);		return array("password"=>"$hash","salt"=>"$salt");	}			function check($password, $salt, $hash) {		$user = crypt($password, $salt);		$sql = $hash;		if ($user == $sql) {			return true;		}	}	?>  