<?php	function sign_in($username, $password) {  // The function that makes the sign in sql query to the db. 		include '../models/encryption.php';		include '../models/sql.php';		$username = addslashes($username); 		$password = encryption($username, $password); 		$sql=			"SELECT				name 			FROM 				users 			WHERE 				username='$username' and password='$password'";		$result = $con -> query($sql);		if (!$result) {			echo "good";		}		$row = mysqli_fetch_array($result);		$count = mysqli_num_rows($result);		if ($count > 0) {			return true;		}		else {			return false;		}	}			function register($name,  $surname,  $age,  $username,  $password, $email) { 		include '../models/sql.php'; 	//The function that adds a new user. It doesn't contain the restriction.		include '../models/encryption.php';		$name = addslashes($name); 		$surname =addslashes($surname);		$age = addslashes ($age);		$email = addslashes($email);  		$username = addslashes($username);		$password = encryption($username, $password); 		$sql =			"INSERT INTO 				`users` ( `name` ,`surname` ,`age` ,`username` ,`password` ,`email` )			VALUES				( '$name',  '$surname',  '$age',  '$username',  '$password',  '$email' )";			if (mysqli_query($con, $sql)) {			return true;			}		else {			return false;		}	}?>